import React, { useState } from 'react';
import {
  Box,
  Card,
  Grid,
  CardContent,
  TextField,
  Button,
  Typography,
  Checkbox,
  FormControlLabel,
  ThemeProvider,
  CssBaseline,
} from '@mui/material';
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          boxShadow: '0 8px 40px -12px rgba(0,0,0,0.3)',
          borderRadius: '16px',
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'center',
          justifyContent: 'center',
        },
      },
    },
  },
  palette: {
    background: {
      default: 'transparent',
    },
    primary: {
      main: '#1A237E',
    },
  },
});

const RegisterPage = () => {
  const [userDetails, setUserDetails] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    acceptTerms: false,
  });

  const handleChange = (e) => {
    const { name, value, checked } = e.target;
    setUserDetails((prevDetails) => ({
      ...prevDetails,
      [name]: name === 'acceptTerms' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (userDetails.password !== userDetails.confirmPassword) {
      alert("Passwords don't match");
      return;
    }

    console.log(userDetails);

    try {
      const response = await fetch('http://localhost:8080/api/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userDetails),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          minHeight: '100vh',
          background: `url("https://img.freepik.com/free-vector/gradient-network-connection-background_23-2148865393.jpg?w=996&t=st=1707914723~exp=1707915323~hmac=ccabc61859da326b02a197822eabbd624e79dcfe309231483541c3e06977056c") center/cover no-repeat`,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <Card
          sx={{
            maxWidth: 'lg',
            display: 'flex',
            flexDirection: { xs: 'column', md: 'row' },
            m: 4,
            overflow: 'hidden',
          }}
        >
          {/* ... rest of the code */}
        </Card>
      </Box>
    </ThemeProvider>
  );
};

export default RegisterPage;
